const Card = ({data}) => {

  let badgeText
  if (data.openSpots === 0) {
    badgeText = 'SOLD OUT'
  } else if(data.openSpots > 0){
    badgeText = 'ONLINE'
  }

  return (
    <div className='card' >

        <div className='state'>
        <p>
          {badgeText} 
        </p>
    </div>


        <img src={data.coverImg} alt="" />
        <div className="ratings">
            <img src={data.Star} alt="" />
            <p>{data.stats.rating}</p>
            <p className='view' >({data.stats.reviewCount})</p>
            <p>{data.location}</p>
        </div>

        <div className="description">
            <h5>
                {data.title}
            </h5>
            <p>
              <span>From {data.price}</span> / person
            </p>
        </div>
    </div>
  )
}

export default Card
