const Card = (props) => {

  let badgeText
  if (props.openSpots == 0) {
    return 'SOLD OUT'
  } else if(props.openSpots > 0){
    return 'ONLINE'
  }

  return (
    <div className='card' >

        <div className='state'>
        <p>
          {badgeText} 
        </p>
    </div>


        <img src={props.img} alt="" />
        <div className="ratings">
            <img src={props.Star} alt="" />
            <p>{props.rating}</p>
            <p className='view' >({props.view})</p>
            <p>{props.location}</p>
        </div>

        <div className="description">
            <h5>
                {props.description}
            </h5>
            <p>
              <span>From {props.price}</span> / person
            </p>
        </div>
    </div>
  )
}

export default Card
