import React, { useState } from 'react'

const StartForm = ({categories}) => {
  console.log(categories)

    const [data, setData] = useState({
        name: '',
        category: '',
        difficulty: ''
    })

    function handleData(e){
        e.target.name = e.target.value
    }
    console.log(data)
  return (
    <div>
        <form action="" onChange={handleData}>
            <input type="text" name={data.name} id="" />
            <select name="" id="">
                <option value="" default>please select a category</option>
                {categories.map((category)=>(
                        <option key={category.id} value={category.id} name={data.category}>
                            {category.name}
                            </option>
                    )
                )}
            </select>
        </form>
    </div>
  )
}

export default StartForm
